"use strict";
Object.defineProperty(exports, Symbol.toStringTag, { value: "Module" });
const olog = console.log;
const sendLog = (level = "info", message, data, error) => {
  olog(
    `robotUtilLog-${encodeURIComponent(JSON.stringify({ level, time: Date.now(), message, data, error }))}`
  );
};
const sendStepLog = (message) => {
  olog(`robotUtilRunStep-${encodeURIComponent(message)}`);
};
function typeToCode(inputItem) {
  if (inputItem.type === "string") {
    return `String(\`${inputItem.value}\`)`;
  } else if (inputItem.type === "number") {
    return `Number(String(\`${inputItem.value}\`))`;
  } else if (inputItem.type === "boolean") {
    return `String(\`${inputItem.value}\`).toLowerCase() == 'true'`;
  }
  return "";
}
exports.olog = olog;
exports.sendLog = sendLog;
exports.sendStepLog = sendStepLog;
exports.typeToCode = typeToCode;
